package fr.hokanosekai.commands;import fr.hokanosekai.managers.JumpsManager;import org.bukkit.Location;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.entity.Player;public class CreateJump implements CommandExecutor {    JumpsManager Jmanager = new JumpsManager();    @Override    public boolean onCommand(CommandSender s, Command cmd, String label, String[] args) {        if (s instanceof Player) {            Player p = (((Player) s).getPlayer());            if (p.isOp()) {                if (args.length == 1) {                    if (!JumpsManager.jumps.containsKey(args[0])) {                        Location loc = p.getLocation();                        Jmanager.createJump(args[0], loc);                        p.sendMessage("§aJump bien créé");                        return true;                    } else {                        p.sendMessage("§cCe jump existe déja");                        return true;                    }                } else {                    p.sendMessage("§cUsage : /createjump <nom du jump>");                    return true;                }            } else {                p.sendMessage("§cVous devez être op");                return true;            }        }        return false;    }}