package fr.hokanosekai.commands;import fr.hokanosekai.Jumps;import fr.hokanosekai.managers.JumpsManager;import fr.hokanosekai.utils.Jump;import org.bukkit.Location;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.entity.Player;import java.util.HashMap;public class RemoveCheckPoint implements CommandExecutor {    Jumps plugin = Jumps.getInstance();    JumpsManager Jmanager = new JumpsManager();    @Override    public boolean onCommand(CommandSender s, Command cmd, String label, String[] args) {        if (s instanceof Player){            Player p = (Player) s;            if (p.isOp()) {                if (args.length == 1){                    Location loc = p.getLocation();                    Jmanager.removeCheckPoint(args[0], loc);                    p.sendMessage("§aCheckpoint enlevé au jump §e" + args[0]);                    return true;                } else {                    p.sendMessage("§cUsage : /removecheckpoint <nom du jump>");                    return true;                }            }            return true;        }        return false;    }}