package fr.hokanosekai.utils;import fr.hokanosekai.Jumps;import org.bukkit.*;public class Blocks {    Jumps plugin;    public Blocks(Jumps plugin) {        this.plugin = plugin;    }    public void setBlock(double x, double y, double z, Material material){        Location loc = new Location(Bukkit.getWorld("world"), x, y, z);        loc.getBlock().setType(material);    }}