package fr.hokanosekai.utils;import fr.hokanosekai.Jumps;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.configuration.file.YamlConfiguration;import java.io.File;import java.io.IOException;public class FileU {    Jumps plugin;    public FileU(Jumps plugin) { this.plugin = plugin; }    public void createFile(String fileName){        if (!plugin.getDataFolder().exists()){            plugin.getDataFolder().mkdir();        }        File file = new File(plugin.getDataFolder(), fileName + ".yml");        if (!file.exists()){            try {                file.createNewFile();            }catch (IOException exception){                exception.printStackTrace();            }        }    }    public File getFile(String fileName){        return new File(plugin.getDataFolder(), fileName + ".yml");    }    public FileConfiguration getConfig(String fileName){        return YamlConfiguration.loadConfiguration(getFile(fileName));    }    public void save(File file, FileConfiguration config){        try {            config.save(file);        } catch (IOException e){            e.printStackTrace();        }    }}