package fr.hokanosekai.utils;import fr.hokanosekai.Jumps;import org.bukkit.Material;import org.bukkit.inventory.ItemStack;import org.bukkit.inventory.meta.ItemMeta;public class Items {    Jumps plugin;    public Items(Jumps plugin) {        this.plugin = plugin;    }    public static ItemStack createItem(String name, Material material){        ItemStack item = new ItemStack(material);        ItemMeta meta = item.getItemMeta();        meta.setDisplayName(name);        item.setItemMeta(meta);        return item;    }}