package fr.hokanosekai.utils;import java.util.ArrayList;import java.util.HashMap;public class Jump {    private final String name;    private final ArrayList<HashMap> coordonnees = new ArrayList<HashMap>();    public Jump(String name){        this.name = name;    }    public ArrayList<HashMap> getCoordonnees() {        return coordonnees;    }    public void addCoordonnees(HashMap<String, Double> loc) {        this.coordonnees.add(loc);    }    public void removeCoordonnees(double[] loc) {        this.coordonnees.remove(loc);    }    public String getName() {        return name;    }}